/* 当前项目各个模块业务处理组件 */
import HomeSwiper from "./childComps/HomeSwiper";
import HomeRecommend from "./childComps/HomeRecommend";
import HomeFeature from "./childComps/HomeFeature";

/* 当前项目业务处理组件 */
import TabControl from "components/content/tabControl/TabControl";
import GoodsList from "components/content/goods/GoodsList";
import BackTop from "components/content/backTop/BackTop";

/* 公共业务处理组件 */
import NavBar from 'components/common/navbar/NavBar';
import Scroll from "components/common/scroll/Scroll";

/* 网络请求方法 */
import {getHomeMultidata,getHomeGoods} from "network/home";

/* 工具方法 */
import {debounce} from "common/utils";

export default {
  name: "Home",
  components: {
    BackTop,
    Scroll,
    GoodsList,
    NavBar,
    TabControl,
    HomeFeature,
    HomeRecommend,
    HomeSwiper,
  },
  data() {
    return {
      banners: [],
      recommends: [],
      goods: {
        'pop':{page: 0,list: []},
        'new':{page: 0,list: []},
        'sell':{page: 0,list: []},
      },
      currentType: 'pop',
      isShowBackTop: false,
      tabOffsetTop: 0,
      isTabFixed: false,
      saveY: 0
    }
  },
  computed: {
    showGoods() {
      return this.goods[this.currentType].list;
    }
  },
  activated() {
    this.$refs.homeScroll.scrollTo(0,this.saveY,1)
    this.$refs.homeScroll.refresh()
  },
  deactivated() {
    this.saveY = this.$refs.homeScroll.getScrollY()
    console.log(this.saveY);
  },
  created() {
    // 1. 请求多个数据
    this.getHomeMultidata();

    // 2. 请求商品数据
    this.getHomeGoods('pop');
    this.getHomeGoods('new');
    this.getHomeGoods('sell');

  },
  mounted() {
    const refresh = debounce(this.$refs.homeScroll.refresh,50)
    // 监听item中图片加载完成
    this.$bus.$on('imageLoad',() => {
      // console.log('...');
       refresh()
    })
  },

  methods: {
    /**
     * 事件监听相关方法
     */
    tabClick(index){
      switch (index) {
        case 0:
          this.currentType = 'pop';
          break;
        case 1:
          this.currentType = 'new';
          break;
        case 2:
          this.currentType = 'sell';
      }
      this.$refs.tabControl1.currentIndex = index
      this.$refs.tabControl2.currentIndex = index
    },
    btnClick() {
      this.$refs.homeScroll.scrollTo(0,0,500);
    },
    contentScroll(position){
      // 1. 判断BackTop是否显示
      this.isShowBackTop = (-position.y) > 1000

      // 2. 判断TabControl是否吸顶
      this.isTabFixed = (-position.y) > this.tabOffsetTop
    },
    upLoadMore() {
      this.getHomeGoods(this.currentType)
    },
    swiperImgLoad() {
      //获取tabControl的offsetTop
      // 每个组件都有一个属性$el：用于获取组件中的元素
      this.tabOffsetTop = this.$refs.tabControl2.$el.offsetTop
    },

    /**
     *网络请求方法
     */
    getHomeMultidata(){
      getHomeMultidata().then(res => {
        // console.log(res);
        this.banners = res.data.banner.list;
        this.recommends = res.data.recommend.list;
      })
    },
    getHomeGoods(type) {
      const page = this.goods[type].page + 1;
      getHomeGoods(type,page).then(res => {
        // console.log(res.data.list);
        this.goods[type].list.push(...res.data.list);
        this.goods[type].page += 1;

        // 完成上拉加载后
        this.$refs.homeScroll.finishPullUp()

      })
    }
  }

}
